1026.source:
function isCorrectCard(card) {
    if(card.cardColor == 'clubs' && card.cardValue == 'seven') {
        return true;
    }
    if(this.playerHasCurrentCard()) {
        return this.getCurrentCard().cardColor == card.cardColor
    }
    return true;
}

1030.source:
function playerHasCurrentCard() {
    var stackColor = this.getCurrentCard().cardColor
    var containsColor = this.currentPlayer.submorphs.detect(function(ea) {
        return ea.cardColor == stackColor 
    });
    if(containsColor != undefined) {
        return true;
    }
    return false; 
}
