424.source:
function saveChanges() {
    // gather annotation informations
    var timestamp = this.getCalendarDate();
    var comment = this.get("Comment").getTextString();
    var user; 
    if (this.annotationPin.world() && this.annotationPin.world().getUserName && 
        this.annotationPin.world().getUserName()) {
        user = this.annotationPin.world().getUserName();
    } 

    lines = comment.split('\n'),
    firstLine = lines[0] + '\n' + user + ' on ' + timestamp;
    //listItem = {
    //        isListItem: true,
    //        string: firstLine,
    //       value: {commentText: comment}
    //};
    //this.get('List1').addItem(listItem);

    var olddata = this.get("Text12").getTextString() + '\n';
    this.get("Text12").setTextString(olddata + firstLine);

    // reset the annotation object
    this.annotation = {
        comment : comment
    };

    // update the annotation morph
    this.restoreAnnotation();
}

432.source:
function close() {
    this.restoreAnnotation(); 
    this.remove();
    this.visibilityIsTemporary = true;
}

440.source:
function getCalendarDate() {
   var months = new Array(13);
   months[0]  = "January";
   months[1]  = "February";
   months[2]  = "March";
   months[3]  = "April";
   months[4]  = "May";
   months[5]  = "June";
   months[6]  = "July";
   months[7]  = "August";
   months[8]  = "September";
   months[9]  = "October";
   months[10] = "November";
   months[11] = "December";
   var now         = new Date();
   var monthnumber = now.getMonth();
   var monthname   = months[monthnumber];
   var monthday    = now.getDate();
   var year        = now.getYear();
   if(year < 2000) { year = year + 1900; }
   var dateString = monthname +
                    ' ' +
                    monthday +
                    ', ' +
                    year;
   return dateString;
}

