function onOK() {
    var desc = $morph('desc').getTextString();
    var est = $morph('inpEst').getTextString(); 
    var rem = $morph('inpRem').getTextString();

    var sticky = this.owner.owner.sticky;

    sticky.init(desc, rem, est);
    sticky.openInWorld();
    sticky.moveBy(pt(550, 120));

    this.owner.owner.remove();

}
