function showConnectionsOf(aMorph) {
    if(!aMorph.isMorph) return;
    aMorph.attributeConnections && aMorph.attributeConnections.forEach(function(con) {
        if(!con.getVisualConnector() && con.sourceObj.isMorph && con.targetObj.isMorph){
            Global.lively.bindings.showConnection(con);
        }
    });
    aMorph.submorphs.forEach(function(ea) { this.showConnectionsOf(ea) }, this)
}

182.source:
function hideConnectionsOf(aMorph) {
    if(!aMorph.isMorph) return;
    aMorph.attributeConnections && aMorph.attributeConnections.forEach(function(con) {
        if(con.getVisualConnector()){
            con.getVisualConnector().hidePermanently();
        }
    });
    aMorph.submorphs.forEach(function(ea) { this.hideConnectionsOf(ea) }, this)
}

