function hasVisibleConnection(aMorph) {
    if(!aMorph.isMorph) return false;
    if (aMorph.attributeConnections 
            && aMorph.attributeConnections.any(function(con) { 
                return !!con.getVisualConnector(); })
    ) 
        return true;
    else
        return aMorph.submorphs.any(function(ea) { 
            return this.hasVisibleConnection(ea) }, this)
}

