function registerEvents() {
    this.onKeyPress = function (evt) {
        var key = String.fromCharCode(evt.getKeyCode());
        switch (key) {
            case "a":
                this.moveBarUp(this.getLeftBar());
                break;
            case "y":
                this.moveBarDown(this.getLeftBar());
                break;
           case "k":
                this.moveBarUp(this.getRightBar());
                break;
            case "m":
                this.moveBarDown(this.getRightBar());
                break; 
            case "n":
                this.startGame();
                break;
            case "q":
                this.stopGame();
                break;
            case "p":
                this.pauseOrResume();
                break;
        }
    }
}

212.source:
function moveBarUp(bar) {
    var barPosition = bar.getPosition();
    if (barPosition.y > 0) {
        var newPosition = barPosition.subPt(new Point(0, this.speed));
        bar.setPosition(newPosition);
    }    
}

221.source:
function moveBarDown(bar) {
   var barPosition = bar.getPosition();
    if (barPosition.y < (this.getExtent().y - bar.getExtent().y)) {
        var newPosition = barPosition.addPt(new Point(0, this.speed));
        bar.setPosition(newPosition);
    }    
}

224.source:
function moveBall() {
	var ball = this.getBall();
        var ballPosition = ball.getPosition();
        var ballY = ballPosition.y;
        var ballX = ballPosition.x;
        var ballExtent = ball.getExtent();
        var ballHeight = ballExtent.y;
        var ballWidth = ballExtent.x;
        this.handleBarCollisions();
        if (ballY < 0 || ballY > (this.getExtent().y - ballHeight)) {
            ball.direction.y *= -1;
        } else if (ballX < 0 || ballX > (this.getExtent().x - ballWidth)) {
            var display = this.get((ball.direction.x === 1) ? "PointsLeft" : "PointsRight");
            display.setTextString(1+parseInt(display.getTextString()));
            ball.direction.x *= -1;
        }
        var positionDiff = pt(ball.direction.x * this.speed, ball.direction.y * this.speed);
        ball.setPosition(ball.getPosition().addPt(positionDiff));
}

