function reset() {
	this.get("PointsLeft").setTextString("0");
        this.get("PointsRight").setTextString("0");
        var dx = (Math.random() > 0.5) ? 1 :- 1;
        var dy = (Math.random() > 0.5) ? 1 :- 1;
        var ball = this.getBall();
        ball.direction = new Point(dx, dy);
        ball.setPosition(pt(this.getExtent().x / 2, this.getExtent().y / 2));
        this.speed = 5;
}

