function handleCollisionsOf(ball, bar) {
        var ballPos = ball.getPosition();
        var ballExtent = ball.getExtent();
        var barPos = bar.getPosition();
        var barExtent = bar.getExtent();

        if (! (ballPos.y > barPos.y && ballPos.y < barPos.y + barExtent.y)) {
            return;
        }
	if (barPos.x < ballPos.x && ballPos.x < barPos.x + barExtent.x) {
            ball.direction.x *= -1;
        } else if (barPos.x < ballPos.x + ballExtent.x && ballPos.x + ballExtent.x < barPos.x + barExtent.x) {
            ball.direction.x *= -1;
        }
}
