function refreshTaskList() {
    var taskList = this.get('taskList'),
        master = this.getGridServerInterface(),
        prevSel = taskList.selection;
    this.reset();
    connect(
        master, 'tasks',
        {$: function(items) {
            // var items = list.collect(function(ea) {
                // return {string: ea.taskName, value: ea, isListItem: true}
            // });
            taskList.setList(items);
            if (prevSel) 
                taskList.setSelectionMatching(prevSel);
        }}, '$')
    master.getTasks();
}

