function taskIsReady(percent) {
    percent = percent || 0;
    percent = Math.round(percent * 100) / 100;
    this.setColorOfTaskStateIndicator(Color.orange);
    this.get('statusText').textString += ': Waiting for workers... (' + percent + '% done)';
}

356.source:
function taskIsNew() {
    this.setColorOfTaskStateIndicator(Color.gray);
    this.get('statusText').textString += ': Waiting for data...';
}

368.source:
function createNewTask() {
    this.world().prompt(
        'Please enter a function as task',
        function(input) {
            if (!input) { alert('aborting'); return };
            var master = this.getGridServerInterface();
            var taskName = master.createTask(input);
            if (taskName)
                this.refreshTaskList();
        }.bind(this),
        'function(a) { return a; }'
    );
}

372.source:
function removeTask() {
    var taskName = this.currentTask;
    this.world().confirm(
        'Really delete ' + taskName + '?' ,
        function(input) {
            if (!input) { alertOK('nothing removed'); return };
            var master = this.getGridServerInterface();
            var result = master.deleteTask(taskName);
            if (result && result.error)
                alert('Error removing task: ' + result.error);
            else
                this.refreshTaskList();
        }.bind(this))
}

