function showTaskInfo() {
    if (!this.currentTask) return;

    var taskName = this.currentTask,
        master = this.getGridServerInterface(),
        statusText = this.get('statusText');
        logText = this.get('logText');

    connect(
        master, 'taskInfo',
        {$: function(taskDef) {
            // var url = 'http://lively-kernel.org/nodejs/' + this.currentTask + '/';
            statusText.textString = taskName;
            // statusText.emphasizeAll({uri: url});

            if (taskDef.results && (taskDef.results == 100))
                this.taskIsDone();
            else if (taskDef.data)
                this.taskIsReady(taskDef.results);
            else
                this.taskIsNew();

            var out = JSON.stringify(taskDef,
                function(key, value) {
                    if (key != 'code')
                        return value;
                }, '  ').split('\n');
            out[out.length - 2] += ',';
            out.splice(out.length - 1, 0, '  "code": ' +
                taskDef.code.split('\n').collect(function(line, idx) {
                    return (idx != 0 ? '  ' : '') + line;
                }).join('\n'));
            logText.textString = out.join('\n');
        }.bind(this) }, '$'
    );
    master.getTaskInfo(taskName);

    // var info = JSON.prettyPrint(this.currentTask);
    // this.get('serverInfoText').textString = info;
}

