function browseResults() {
    if (!this.currentTask) return;

    var master = this.getGridServerInterface(),
        taskName = this.currentTask;

    function formatResultSet(results) {
        var input = results.input;
        var output = results.output;

        function assignInToOut(input, output, paramPos) {
            paramPos = paramPos || [];

            if (input[paramPos.length]) {
                input[paramPos.length].each(function(p, idx) {
                    assignInToOut(input, output, paramPos.concat(idx));
                });
            } else {
                var res = output;
                var paramList = paramPos.collect(function(pos, idx) {
                    res = res[pos];
                    return JSON.serialize(input[idx][pos]);
                });
                paramList = JSON.serialize(paramList.toString());
                paramList = paramList.substr(1, paramList.length - 2);
                res = JSON.serialize(res);
                formattedList.push('(' + paramList + ') => ' + res);
            }
        }

        var formattedList = [];
        assignInToOut(input, output);

        return formattedList.join('\n');
    }

    connect(
        master, 'taskResults',
        {$: function(results) {
            if (results == null)
                return;

            var resultText = this.world().addTextWindow({
                title: 'Browsing results for ' + taskName, 
                content: formatResultSet(results),
                syntaxHighlighting: false
            });
            // resultText.accessibleInInactiveWindow = true;
            resultText.setClipMode('auto');
            resultText.applyStyle({
                resizeWidth: true,
                resizeHeight: true
            });
            resultText.setFontSize(8);
            resultText.setFontFamily('Monaco,monospace');
        }.bind(this) }, '$')
    master.getTaskResults(taskName);
}

