function resetTaskData() {
    if (!this.currentTask) return;

    var master = this.getGridServerInterface(),
        taskName = this.currentTask;

    this.world().prompt(
        'Please enter a data set definition:',
        function(dataSet) {
            if (!dataSet) { alert('aborting'); return };

            this.world().prompt(
                'Please enter the number of tasks (cancel = auto):',
                function(noTasks) {
                    master.setTaskData(taskName, eval(dataSet), noTasks);
                    this.refreshTaskList();
                }.bind(this),
                '5'
            );
        }.bind(this),
        '[[1,2,3,4,5]]'
    );
}

