function switchState() {
    this.state = (this.state == undefined ? 0 : this.state + 1);

    switch (this.state) {
    case 0: // inactive
        this.get('stateChanger').setLabel('activate');
        this.workerIsInactive();
        break;
    case 1: // active
        this.get('stateChanger').setLabel('deactivate');
        this.workerIsActive();
        this.startGridTask();
        break;
    default: // shutting down
        this.get('stateChanger').setLabel('stopping...');
        this.workerIsStopping();
        this.stopTask.bind(this).delay(1);
        break;
    }
}

