function startGridTask() {
    var master = this.getGridServerInterface();

    // TODO: long-polling?
    var conn = connect(master, 'task', { $: function(task) {
        if (!task) {
            this.get('currentTask').textString = '';
            this.get('workerId').textString = '';

            this.pollingID = setTimeout(this.startGridTask.bind(this), 5000);
        } else {
            this.task = task;

            if (this.pollingID) {
                clearTimeout(this.pollingID);
                delete this.pollingID;
            }

            this.get('currentTask').textString = this.task.taskName;
            this.get('workerId').textString = this.task.taskID;

            var uri = URL.codeBase.
                withFilename('../users/markoroeder/grid/worker.js').
                withRelativePartsResolved();

            this.worker = newWorker(uri);
            this.worker.addEventListener('message',
                this.onWorkerNotification.bind(this));

            this.worker.postMessage(this.task);
        }
    }.bind(this) }, '$', { removeAfterUpdate: true });

    if (!master.getOpenTask()) {
        conn.disconnect();
        alertOK('No more tasks available.');
        this.switchState.bind(this).delay(1);
    }
}

