function onWorkerNotification(event) {
    var msg = event.data;
    if (msg.result && this.task) {
        var master = this.getGridServerInterface();
        master.uploadResult(this.task, msg.result);

        // TODO: continuously do jobs
        this.switchState();
    }
}

