function stopTask() {
    if (this.worker) {
        this.worker.terminate();
        this.worker = null;
    }
    if (this.pollingID) {
        clearTimeout(this.pollingID);
        delete this.pollingID;
    }

    var gray = Color.gray.darker();
    this.get('currentTask').emphasizeAll({ color: gray });
    this.get('workerId').emphasizeAll({ color: gray });

    this.state = -1;
    this.switchState();
}

