function receiveData(data) {
    var highLimit = Number(this.get('highLimit').textString),
    emitRate = Number(this.get('emitRate').textString) * 1000,
    data = Number(data),
    now = new Date();

    var average = this.updateAverage(now, data);
    if (this.lastEventDate < (now - emitRate)) {
        if (average > highLimit && (this.lastEventMsg == 'low')) {
            lively.bindings.signal(this, 'highEvent', average);
            this.lastEventDate = now;
            this.lastEventMsg = 'high';
        } else if (average <= highLimit && (this.lastEventMsg == 'high')) {
            lively.bindings.signal(this, 'lowEvent', average);
            this.lastEventDate = now;
            this.lastEventMsg = 'low';
        }
    }
}

