function updateAverage(date, data) {
    var history = Number(this.get('emitRate').textString) * 1000;

    // add new element
    this.averageData.push({date: date, data: data});

    // remove old elements
    while ((this.averageData.length < 1) || ((this.averageData[0].date.getTime() + history) < date.getTime())) {
        this.averageData.shift();
    }

    // calculate average
    for(var i=0,sum=0;i<this.averageData.length;sum+=this.averageData[i++].data);
    var average = (sum/i);
    lively.bindings.signal(this, 'average', average);

    return average;
}

