function registerWorkspace() {
    this.setupConnection();

    var name = this.get("MyName").textString;
    this.socket.emit("register", name, function (result) {
        if (result) {
            alertOK("Registered as " + name);
        } else {
            alertOK("Already registered as " + name);
        }
    });
}

