function remoteDoit() {
    var source = this.get("SourceText").textString;
    var workspace = this.get("TargetParticipantName").textString;

    if (typeof(this.socket) == 'undefined') {
        alert("I'm afraid I can't do that, Dave.");
        return;
    }

    alertOK('Evaling on ' + workspace);
    this.socket.emit("doit", workspace, source, function (result) {
        console.log(result);
        alertOK("Result: " + result);        
    });
}

