function setupConnection() {    
    var resource = 'nodejs/DistributedDoitServer/socket.io';
    var serverURL = 'http://lively-kernel.org/' + resource;

    if (typeof(io) == "undefined") {
        var socketioURL = serverURL + '/socket.io.js';
        console.log('Loading Socket.io from ' + socketioURL); 
        var source = JSLoader.getSync(socketioURL);
        window.eval(source);
    } else {
       console.log("Socket.io is already loaded...");
    }

    this.teardownConnection();
    console.log('Creating new connection to ' + resource);
    this.socket = io.connect(null, {resource: resource, 'force new connection': true});

    this.socket.on('doit', function(code, fn) {
        var result = eval(code);
        fn(result);
    });
    this.socket.on('error', function(msg) {
        alert("Socket.io Error: " + msg);
    });
}

