function printResults() {
    this.get('ResultList').setList(Properties.own(this.allScripts)
        .collect(function(ea) {
            return {scriptName: ea, counter: this.allScripts[ea].length}
        }, this)
        .sortBy(function(ea) { return ea.counter})
        .reverse()
        .collect(function(ea) { return ea.scriptName + " " + ea.counter }))

}

