function scanPartsBin(finish) {
    // scans all PartsSpaces of a PartsBin
    wr = new WebResource(URL.codeBase.withFilename('PartsBin/'))
    wr.getSubElements(1)
    var all = wr.subCollections
    var i = 0;
    all.collect(function(ea) { return ea.getURL().filename()})
        .doAndContinue(function(next, ea) {
            this.get('ProgressBar').setValue( i++ / all.length);
            this.get('ProgressBar').setLabel("scan " + ea)
            if (this.stopRunning) return;
            (function() {this.scanPartSpace("PartsBin/" + ea, next)}).bind(this).delay(0.3)
        }, finish, this)

}

