236.source:
function scanPartSpace(partSpaceName, finish) {
    // does the tokenization scan in all parts of a PartsSpace
    this.log("== Scan PartsSpace " + partSpaceName + " ==")
    items = lively.PartsBin.partsSpaceNamed(partSpaceName).load().getPartItems();
    var self = this;
    items.doAndContinue(function(next, ea) {
        partScanner = {
            onLoad: function(part) {
                self.log("scan part " + part.getName())
                try {
                    self.scanPart(part)
                } catch(e) {
                    self.log("Error during scanning: \n" + e) 
                }finally {
                    next()
                }
            }
        }
        connect(ea, 'part', partScanner, 'onLoad')
        try {
            ea.loadPart(true)
        } catch(e) {
            self.log("Error during load part, continue any way: \n" + e)
            next();
        }
    }, finish)
}

244.source:
function scanPart(part) {
    // Tokenizes all scripts in all objects in a part
    this.withAllScriptsDo(part, function(obj, prop) {
        var script = obj[prop]
        if (!script) return;
        m = script.toString().match(/[A-Za-z$][A-Za-z$0-9]+/g)
        m.forEach(function(key) {
            this.tallyScript(key, part, obj, prop)
        }, this)
    }.bind(this))
}

248.source:
function withAllScriptsDo(obj, func) {
    Functions.own(obj).each(function(ea) {
        func(obj, ea) 
    })
}

252.source:
function tallyScript(key, part, obj, scriptName) {
    // is called for every match in every script of every object in every parts
    if (!this.allScripts[key])
        this.allScripts[key] = [];
    this.allScripts[key].push({
        partSpaceName: part.getPartItem().partsSpaceName,
        partName: part.getName(), 
        objName: obj.getName(), 
        scriptName: scriptName})
}

