function showConnections(container) {
    $world.withAllSubmorphsDo(function(ea) {
        if (!ea.isEpiMorph && ea.attributeConnections) {
            for (var i = 0; i < ea.attributeConnections.length; i++)
                this.showConnection(ea.attributeConnections[i], container);
        }
    }, this)
}

