function showConnection(con, container, duration, value) {
        // for testing
        if (!con) {
            con = this.get("Slider").attributeConnections[1]
        }    
        duration = 1

        var color = Color.blue;
        container = container || $world;
        var line;
        var source = con.sourceObj,
        target = con.targetObj;
        if (!(source instanceof Morph) || !(target instanceof Morph)) return;
        if (!(target instanceof Morph) || !(target instanceof Morph)) return;

        if (!this.linesCache) {
            this.linesCache = {};
        };
        if (source === target) return; // do nothing for self connections yet

        var points = [
                source.worldPoint(source.shape.bounds().center()),
                target.worldPoint(target.shape.bounds().center())
            ]
        if (!this.linesCache[con.toString()]) {
            line = new lively.morphic.Path(points);
            line.setBorderColor(color)
            this.linesCache[con.toString()] = line   
            line.label1 = new TextMorph(new Rectangle(0,0,50,20), con.sourceAttrName);
            line.label1.beLabel();
            line.label1.setTextColor(color)
            line.label2 = new TextMorph(new Rectangle(0,0,50,20), "");
            line.label2.beLabel();
            line.label2.setTextColor(color)
            line.label3 = new TextMorph(new Rectangle(0,0,50,20), con.targetMethodName);
            line.label3.beLabel();
            line.label3.setTextColor(color)
        } else {
            line = this.linesCache[con.toString()]
            line.setVertices(points);
        };
        line.label2.setTextString("" + value)
        container.addMorph(line.label1)
        container.addMorph(line.label2)
        container.addMorph(line.label3)
        line.label1.align(
            line.label1.bounds().topLeft(),
            points[0].addPt(points[1].subPt(points[0]).scaleBy(0.2)))
        line.label2.align(
            line.label2.bounds().center(),
            points[0].addPt(points[1].subPt(points[0]).scaleBy(0.5)))
        line.label3.align(
            line.label3.bounds().bottomRight(),
            points[0].addPt(points[1].subPt(points[0]).scaleBy(0.8)))

        container.addMorph(line)

        var color = Color.blue.withA(0.5)


        if (line.isFadingOut) {
            line.setOpacity(1)
            line.label1.setOpacity(1);
            line.label2.setOpacity(1);
            line.label3.setOpacity(1);

            return;
        }

        line.isFadingOut = true;
        var steps = 10;
        var self = this;
        var fadeOut = function() {
            if (line.getOpacity() > 0) {
                var newOpacity = line.getOpacity() - (1 / steps);
                line.label1.setOpacity(newOpacity);
                line.label2.setOpacity(newOpacity);
                line.label3.setOpacity(newOpacity);

                line.setOpacity(newOpacity);

                fadeOut.delay(duration / steps)            
            } else {
                line.remove()
                line.label1.remove()
                line.label2.remove()
                line.isFadingOut = false;
                delete self.linesCache[con.toString()] 
            }
        };
        fadeOut();


}

