function ensureContainer() {
    this.hide()

    // we don't want to interfere with object structure, so we put the labels
    // in a separate object
    helpContainer = Morph.makeRectangle($world.shape.bounds())
    helpContainer.name = 'HelpContainer';

    helpContainer.ignoreEvents();
    helpContainer.disableEvents();
    helpContainer.setOpacity(1);
    helpContainer.setFill(null)
    helpContainer.setBorderWidth(0)

    helpContainer.openInWorld();

    return helpContainer
}

