function install() {
    var self= this;
    cop.create('AnimateConnectionsLayer').refineClass(AttributeConnection, {
        update: function(newValue, old) {
            cop.withoutLayers([AnimateConnectionsLayer], function(){
                // alertOK("update " + this);
                try {
                    self.showConnection(this, self.container, 3, newValue)
                } catch(e) {
                    alertOK("silent fail " + e)
                }
            }.bind(this))
            return cop.proceed(newValue, old);
        }
    })
}
