129.source:
function morphUnderCursor() {
    var that = this,
        world = lively.morphic.World.current();    

    return world.morphsContainingPoint(world.firstHand().getPosition()).detect(
        function(ea) {
            return  !ea.isPlaceholder &&
                    !ea.isHalo &&
                    (!ea.owner || !ea.owner.isHalo) &&
                    !(ea === that);
    });
}

135.source:
function onFire() {
    var hand = lively.morphic.World.current().firstHand(),
        highlight = this.highlightRectangle,
        that = this;

    if (this.isTracking()) {
        this.removeHighlighting();
    } else {
        this.world().addMorph(highlight);
        hand.highlightConnection = connect(hand, "scrollFocusMorph", highlight, "update");
        highlight.bringToFront();
        if (!this.currentTarget() || !this.currentTarget().world()) {
            highlight.setExtent(pt(0,0));
        }
    }
}

139.source:
function removeHighlighting() {
    var hand = this.world().firstHand();

    if (this.highlightRectangle) {
        this.highlightRectangle.remove();
    }

    hand.attributeConnections.removeAt(
        hand.attributeConnections.indexOf(hand.highlightConnection));
    hand.highlightConnection = null;
}

143.source:
function onMouseMove(evt) {
    var target = this.currentTarget();
    if (target && target.world() && !this.isHighlighting()) {
        this.highlightRectangle.update(target);
        this.world().addMorph(this.highlightRectangle);
        this.highlightRectangle.bringToFront();
        this.targetHighlight = this.highlightRectangle;
    }
}

