function reset() {
    this.highlightRectangle.attributeConnections = [];

    var r = this.highlightRectangle;
    r.isToolMorph = true
    disconnectAll(r)
    connect(r, "onMouseMove", this.highlightRectangle, "updateOnMove")
    connect(r, "onMouseUp", this, "removeHighlighting")
    connect(r, "onMouseUp", this, "setTarget",
            {converter: function () {
                return this.sourceObj.morphUnderCursor();
            }
        })
}

