function installLayers() {

// cop.makeFunctionLayerUnaware(Function.prototype, "addMethods")
// cop.makeFunctionLayerUnaware(Function.prototype, "addCategorizedMethods")

cop.create("ChangeRecorderLayer").refineObject(Function.prototype, {
    addCategorizedMethods: function(category, source) {
        if (ChangeRecorderLayer.recorder) {
            ChangeRecorderLayer.recorder.recordAddCategorizedMethods(this, category, source)   
        }
        // alertOK("addCategorizedMethods " + category + " source: " + source );
        return cop.proceed.apply(this, arguments)
    },

    addMethods: function(/*...*/) {
        // alertOK("addMethods" + $A(arguments))
        return cop.proceed.apply(this, arguments)
    }
})

