function recordAddCategorizedMethods(obj, category, source) {
    var objPath = obj.type;
    var date = new Date();
    var userName = $world.getUserName();

    Object.keys(source).forEach(function(ea) {
        var value = {
            objPath: objPath, 
            kind: 'addMethod',
            date: date,
            userName: userName, 
            categoryName: category,
            methodName: ea, 
            functionBody: source[ea].toString(),
            toString: function() {
                return this.objPath + ">>>" + this.methodName 
                    + ' (' + this.userName + ", " + this.date.format("yyyy-mm-dd HH:MM")  + ")" 

            }.asScript()
        };
        var prevFunctionBody = obj.prototype[ea];
        if (prevFunctionBody) {
            value.prevFunctionBody = prevFunctionBody.toString();
            value.prevSourceModule = prevFunctionBody.sourceModule.namespaceIdentifier;
        }
        this.addChange(value);

    }, this)

}

