function replayChange(change) {

    if (change.kind == "addMethod") {    
        alertOK("replay " + change.kind + " " + change.objPath + ">>" + change.methodName);
        var obj;
        try {
            obj = eval(change.objPath) 
        } catch(e) { 
            alert("could not resolve " + change.objPath)
            return 
        };
        var source = {};
        var func;
        try {
            func = eval("(" + change.functionBody + ")")
        } catch(e) { 
            alert("could eval " + change.functionBody)
            return 
        };

        source[change.methodName] = func;
        obj.addMethods(source)
    }    
}

