function onFire() {
    if (!this.owner.isAligning) {
        this.owner.isAligning = true;
        this.setLabel("[following]") 
        this.owner.startStepping(1000, "follow")
    } else {
        this.setLabel("[not following]") 
        this.owner.stopStepping()

        this.owner.isAligning = false;

    }

}

