function install() {

var recorder = this;

cop.create("EventRecordLayer").refineClass(lively.morphic.Text, {

    onKeyPress: function(evt) {
        cop.withoutLayers([EventRecordLayer], function() {
            recorder.recordEvent(evt)
        })
        return cop.proceed(evt)        
    },
})



}

406.source:
function recordEvent(evt) {
    if (! this.isRecording) return;
    if (!this.events) this.events = [];

    var clonedEvent = {};
    clonedEvent.CONSTRUCTOR_NAME = evt.constructor.name;


    Properties.own(evt).forEach(function(ea) {
        var value = evt[ea];
        if (value instanceof Object || value instanceof Array) {
        } else {
            clonedEvent[ea] = value
        }
    })



    this.events.push(clonedEvent)
}

410.source:
function updateList() {
    if (!this.events) return;
    this.get('EventList').setList(this.events.collect(function(ea) {
        return { 
            isListItem: true,
            string: ea.type + ' ' + String.fromCharCode(ea.charCode), 
            value: ea
        }
    }))
}

