function updateFilter() {
    var text = this.get('FilterSource').textString
    try {
    var selectFilter = new RegExp(this.get('FilterList').textString,"g")
    } catch(e) {
        this.get('FilterList').setStatusMessage("" +e)
    }
    var lastCaller = 'nothing';
    text = text.split('\n')
        .select(function(line) {
            return line.match(selectFilter)
        })
        .reject(function(line) {
            return line.match(/morphicGetter/)
        })
        .collect(function(line) {
            var m = line.match(/( *)([A-Z].*)>>(.*)/);
            if (!m) return line;
            var caller = m[2];
            if (caller == lastCaller) {
                return m[1] + Strings.indent('', ' ', m[2].length) 
                    + " >>> " + m[3]
            }
            lastCaller = caller
            return line
        })
        .join('\n')
    this.get('FilterTarget').setTextString(text);
    this.get('FilterTarget').emphasizeRegex(/.*>>/g, {color: Color.darkGray})
    this.get('FilterTarget').emphasizeRegex(/\(.*?\)/g, {color: Color.darkGray})
    this.get('FilterTarget').emphasizeRegex(selectFilter, {color: Color.red})

}
