176.source:
function cleanObject(obj) {
    this.resetLog()
    if (obj === this.world()) {
        this.log("Go clean the world yourself!")
        return
    }
    this.log("clean " + obj)
    var originalSize = this.sizeOfObj(obj)

    this.stripPartsBinMetaInfo(obj)
    var withoutMetaInfoSize = this.sizeOfObj(obj)
    var difference =  originalSize - withoutMetaInfoSize
    this.log("striped " + difference + " bytes " + " "
        + "(" + (difference / originalSize * 100) + "%) partsBinMetaInfo\n")

    this.stripDerivationIds(obj)
    var withoutDerivationIDsSize = this.sizeOfObj(obj)
    var difference2 =  withoutMetaInfoSize - withoutDerivationIDsSize
    this.log("striped " + difference2 + " bytes " + " "
        + "(" + (difference2 / originalSize * 100) + "%) derivationIds\n")


    var toalDifference = difference2 + difference;
    this.log("striped " + toalDifference + " bytes from " + originalSize + "bytes "
        + "(" + (toalDifference / originalSize * 100) + "%) total\n")

}

