function stripPartsBinMetaInfo(obj) {
    if (obj.partsBinMetaInfo) {
        this.log("strip partsBinMetaInfo " + obj.name)
        delete obj.partsBinMetaInfo
    }
    if (obj.submorphs) {obj.submorphs.each(function(ea) {
        this.stripPartsBinMetaInfo(ea)
    }.bind(this))}
}

192.source:
function serializeObject(obj) {
    var serializer = lively.persistence.Serializer.createObjectGraphLinearizerForCopy();
    serializer.showLog = false;
    var copyPlugin = new CopyOnlySubmorphsPlugin();
    copyPlugin.root = obj;
    serializer.addPlugin(copyPlugin);
    serializer.copy
    var jso = serializer.serializeToJso(obj);
    return jso
}

