function stripDerivationIds(obj) {
    if (obj.derivationIds) {
        this.log("strip derivationIds " + obj.name)
        delete obj.derivationIds
    }
    if (obj.submorphs) {obj.submorphs.each(function(ea) {
        this.stripDerivationIds(ea)
    }.bind(this))}
}

