function morphUnderCursor() {
    var that = this,
        world = lively.morphic.World.current();    

    return world.morphsContainingPoint(world.firstHand().getPosition()).detect(
        function(ea) {
            return  !ea.isPlaceholder &&
                    !ea.isHalo &&
                    (!ea.owner || !ea.owner.isHalo) &&
                    !(ea === that);
    });
}

