768.source:
function makeHandle() {
    var m = lively.morphic.Morph.makeRectangle(0,0,10,10);
    m.disableGrabbing()
    m.enableDragging()
    m.connector = this;
    // move real behavior to a place which is easier to develop
    // do not use bind, because we like late binding 
    m.addScript(function onDragStart(evt) {
        this.connector.onDragStartInHandle(this, evt)
    })
    m.addScript(function onDrag(evt) {
        this.connector.onDragInHandle(this, evt)
    })
    m.addScript(function onDragEnd(evt) {
        this.connector.onDragEndInHandle(this, evt)
    })
    m.isEpiMorph = true;
    return m
}

