function onDragInHandle(handle, evt) {

    var pos = handle.getPositionInWorld();
    var delta = evt.hand.getPosition().subPt(pos);
    handle.moveBy(delta)
    pos = handle.getPositionInWorld();
    if (handle.controlPoint)
        handle.controlPoint.setGlobalPos(pos.addPt(pt(5,5)))
    var morphs = this.owner.morphsContainingPoint(pos)
    morphs = morphs.reject(function(ea) {
        return ea.isWorld || (ea === handle) || (ea === this) || 
            (ea === this.owner)
    }, this)
    if (this.selectedMorphHighlights) 
        this.selectedMorphHighlights.invoke('remove')
    this.selectedMorph = morphs.first();
    this.selectedMorphHighlights = show(this.selectedMorph)
    handle.startPos = evt.getPosition();
}

792.source:
function onDragEndInHandle(handle, evt) {
    if (this.selectedMorphHighlights) 
        this.selectedMorphHighlights.invoke('remove')

    if (handle.isFromHandle) this.connectFrom(this.selectedMorph)
    if (handle.isToHandle) this.connectTo(this.selectedMorph)
    handle.remove()
    this.updatePosition()
}

