function connectFrom(morph) {
    if (morph && morph === this.toMorph) return; // not allowed

    this.disconnectMorph(this.fromMorph)
    this.fromMorph = morph;
    this.connectMorph(this.fromMorph)
}

800.source:
function connectTo(morph) {

    if (morph && morph === this.fromMorph) return; // not allowed

    this.disconnectMorph(this.toMorph)
    this.toMorph = morph;
    this.connectMorph(this.toMorph)
}

