816.source:
function lineIntersect(a1, a2, b1, b2){
    // inspired by http://stackoverflow.com/questions/3746274/line-intersection-with-aabb-rectangle
    var b = a2.subPt(a1);
    var d = b2.subPt(b1);
    var bDotDPerp = b.x * d.y - b.y * d.x;

    // if b dot d == 0, it means the lines are parallel so have infinite intersection points
    if (bDotDPerp == 0)
        return null;

    var c = b1.subPt(a1);
    var t = (c.x * d.y - c.y * d.x) / bDotDPerp;
    if (t < 0 || t > 1)
        return null;

    var u = (c.x * b.y - c.y * b.x) / bDotDPerp;
    if (u < 0 || u > 1)
        return null;

    return a1.addPt(b.scaleBy(t));
}

820.source:
function intersectMorphLinePoints(morph, a1, a2) {
    var points = this.globalShapePathFromMorph(morph);
    var intersections = [];
    points.inject(points.last(), function(last, ea) {
        var p = this.lineIntersect(a1, a2, last, ea)
        if (p) intersections.push(p)
        return ea
    }, this)
    return intersections
}

824.source:
function intersectMorph(morph) {
    var line = this;
    return this.intersectMorphLinePoints(morph,
        line.controlPoints[0].getGlobalPos(), line.controlPoints[1].getGlobalPos())
}

828.source:
function globalShapePathFromMorph(morph) {
    var b = morph.shape.bounds();
    var p1 = morph.worldPoint(b.topLeft()),
        p2 = morph.worldPoint(b.topRight()),
        p3 = morph.worldPoint(b.bottomRight()),
        p4 = morph.worldPoint(b.bottomLeft());
    return [p1, p2, p3, p4]
}

