857.source:
function intersects(a1, a2, b1, b2){
    // inspired by http://stackoverflow.com/questions/3746274/line-intersection-with-aabb-rectangle
    var b = a2.subPt(a1);
    var d = b2.subPt(b1);
    var bDotDPerp = b.x * d.y - b.y * d.x;

    // if b dot d == 0, it means the lines are parallel so have infinite intersection points
    if (bDotDPerp == 0)
        return null;

    var c = b1.subPt(a1);
    var t = (c.x * d.y - c.y * d.x) / bDotDPerp;
    if (t < 0 || t > 1)
        return null;

    var u = (c.x * b.y - c.y * b.x) / bDotDPerp;
    if (u < 0 || u > 1)
        return null;

    return a1.addPt(b.scaleBy(t));
}

