874.source:
function draw() {
    if (!this.chart) this.chart = this.getChart();
    this.chart.selectAll('svg').remove();

    var width = this.getExtent().x,
        height = this.getExtent().y;

    var labelAnchors = this.data.nodes.reduce(function(lst, node) {
        lst.push({ node: node });
        lst.push({ node: node });
        return lst;
    }, []);
    var labelAnchorLinks = this.data.nodes.collect(function(node, idx) {
        return { source: idx * 2, target: idx * 2 + 1, weight: 1 };
    });

    var forceLabel = d3.layout.force()
        .gravity(0)
        .linkDistance(0)
        .linkStrength(8)
        .charge(-100)
        .size([width, height])
        .nodes(labelAnchors)
        .links(labelAnchorLinks)
	.start();

    var color = d3.scale.category20c();

    var force = d3.layout.force()
        .gravity(1)
        .linkDistance(50)
        .linkStrength(function(x) {
            return x.weight * 10;
        })
        .charge(-3000)
        .size([width, height])
        .nodes(this.data.nodes)
        .links(this.data.links)
        .start();

    var svgOuter = this.chart.append('svg')
        .attr('width', width)
        .attr('height', height)
        .call(d3.behavior.zoom().on("zoom",  function() {
            svg.attr("transform","translate(" + 
                d3.event.translate[0] + "," +  d3.event.translate[1] + 
                ") scale(" +  d3.event.scale + ")");
        }))

    var svg = svgOuter.append ("g");

    var link = svg.selectAll('line.link')
        .data(this.data.links)
      .enter().append('line')
        .style('stroke', '#999999')
        .style('stroke-opacity', 0.6)
        .style('stroke-width', function(d) { return d.weight; });

    var node = svg.selectAll('circle.node')
        .data(this.data.nodes)
      .enter().append('circle')
        .attr('r', 5)
        .style('stroke', '#FFFFFF')
        .style('stroke-width', '1.5px')
        .style('fill', function(d) { return color(1); })
        .call(force.drag);

    var anchorLink = svg.selectAll('line.anchorLink')
        .data(labelAnchorLinks);

    var anchorNode = svg.selectAll('g.anchorNode')
        .data(forceLabel.nodes())
      .enter().append('g');
    anchorNode.append('circle')
        .attr('r', 0)
        .style('fill', '#FFFFFF');
    anchorNode.append('text')
        .text(function(d, i) { return i % 2 == 0 ? '' : d.node.name; })
        .style('fill', '#555555')
        .style('font-family', 'Arial')
        .style('font-size', 12);

    function updateLink() {
        this.attr('x1', function(d) { return d.source.x; })
            .attr('y1', function(d) { return d.source.y; })
            .attr('x2', function(d) { return d.target.x; })
            .attr('y2', function(d) { return d.target.y; });
    }
    function updateNode() {
        this.attr('transform', function(d) {
            return 'translate(' + d.x + ',' + d.y + ')';
        });
    }

    force.on('tick', function() {
        forceLabel.start();

        node.call(updateNode);
        anchorNode.each(function(d, i) {
            if (i % 2 == 0) {
                d.x = d.node.x;
                d.y = d.node.y;
            } else {
                var b = this.childNodes[1].getBBox();

                var diffX = d.x - d.node.x;
                var diffY = d.y - d.node.y;

                var dist = Math.sqrt(diffX * diffX + diffY * diffY);

                var shiftX = b.width * (diffX - dist) / (dist * 2);
                shiftX = Math.max(-b.width, Math.min(0, shiftX));
                var shiftY = 5;
                this.childNodes[1].setAttribute('transform',
                    'translate(' + shiftX + ',' + shiftY + ')');
            }
        });
        anchorNode.call(updateNode);

        link.call(updateLink);
        anchorLink.call(updateLink);
    });
}

882.source:
function initializeData() {
    this.data = {nodes: [], links: []}
    this.nodes = {}

    var index = 0;
    this.target.derivationIds.inject(null ,function(last, ea) {
        var node = {name: ea, index: index++}
        this.nodes[ea] = node;
        this.data.nodes.push(node)

        if (this.nodes[last])  {
            this.data.links.push({
                source: this.nodes[last].index,
                target: this.nodes[ea].index, weight: 1})
        }
        return ea;
    }, this)


}

