935.source:
function hasChanged() {
    var cleanText = function (string) {
        var source = string.trim();
        if (source.substring(0,2) === "//") {
            // removes annotation line
            source = source.substring(source.indexOf("\n"), source.length);
            source = source.trim();
        }
        if (source === 'undefined' || source === 'null') source = '';
        return source;
    }
    var cleanedTextString = cleanText(this.textString);
    var cleanedLastSource = cleanText(this.lastSaveSource);
    return cleanedTextString !== cleanedLastSource;
}

939.source:
function displayStatus(msg, color, delay) {
    if (!this.statusMorph) {
        this.statusMorph = new TextMorph(pt(100,25).extentAsRectangle());
        this.statusMorph.applyStyle({borderWidth: 1, strokeOpacity: 0, borderColor: Color.gray});
        this.statusMorph.setFill(this.owner.getFill());
        this.statusMorph.setFontSize(11);
        this.statusMorph.setAlign('center');
        this.statusMorph.setVerticalAlign('center');
    }
    this.statusMorph.setTextString(msg);
    this.statusMorph.centerAt(this.innerBounds().center());
    this.statusMorph.setTextColor(color || Color.black);
    this.addMorph(this.statusMorph);
    (function() { this.statusMorph.remove() }).bind(this).delay(delay || 2);    
}

