function setTargetToListSelection(selection) {  
    this.removeList();
    if (!selection) return;
    if (this.owner.scriptPane.hasChanged()) {
        var that = this;
        var callback = function (confirmed) {
            if (confirmed) that.setTargetOfPane(selection);
        }
        this.owner.confirmUnsavedChanges(callback);
    } else {
        this.setTargetOfPane(selection);
    }
}

