function runScript() {
    var scriptName = this.get("ObjectEditorScriptList").selection;
    if (!scriptName || !this.target) return;

    this.lastError = null;
    this.get('Log').setList([])
    this.get('ErrorMessage').textString = ''
    try {
        this.target[scriptName]();
    } catch(e) {
        this.lastError = e
        this.get('ErrorMessage').textString = e
        this.get('Log').setList(
            this.parseErrorStack(this.lastError.stack)
                .select(function(ea) {
                    return ea.full.match("at "+this.target.constructor.name)
                }, this)
                .collect(function(ea) {
                    return {string: '' + ea.full.replace(/\(.*/, ""), value: ea}
                }, this)
        )
    }
}

